#[test]
fn test_code_interpreter_tool_load_json() {
    let json = r####"
{
  "kind": "code_interpreter",
  "fileIds": [
    "file1",
    "file2"
  ]
}
"####;
    let result = CodeInterpreterTool::from_json(json);
    assert!(
        result.is_ok(),
        "Failed to load from JSON: {:?}",
        result.err()
    );
    let instance = result.unwrap();
    assert_eq!(instance.kind, "code_interpreter");
}

