#[test]
fn test_function_tool_load_json() {
    let json = r####"
{
  "kind": "function",
  "parameters": {
    "properties": {
      "firstName": {
        "kind": "string",
        "value": "Jane"
      },
      "lastName": {
        "kind": "string",
        "value": "Doe"
      },
      "question": {
        "kind": "string",
        "value": "What is the meaning of life?"
      }
    }
  },
  "strict": true
}
"####;
    let result = FunctionTool::from_json(json);
    assert!(
        result.is_ok(),
        "Failed to load from JSON: {:?}",
        result.err()
    );
    let instance = result.unwrap();
    assert_eq!(instance.kind, "function");
    assert!(instance.strict.is_some(), "Expected strict to be Some");
    assert_eq!(instance.strict.as_ref().unwrap(), &true);
}

